#if !defined(_AG34410A_H)
#define _AG34410A_H

#include "visa32_device.h"
///万用表AG34410A的类
/** 
 *	AG24410A为6 1/2万用表，其在测试领域应用广泛
 */
class AG34410A : public visa32_device	{
private:
	///设备名称字串，比如ag34410a等
	char* device_id;
public:
	double read_res(const DWORD param1) const;
	///default constructor
	AG34410A();
	///destructor
	~AG34410A();
	///选择USB资源名
	virtual	bool select_visa_usb_device()	const;
	///清仪器	
	virtual	void clear()	const;
	///复位仪器	
	virtual	void reset()	const;
	///识别仪器名称字串	
	virtual	void identify()	const;
	///读取直流电流
	/** 
	 *	@param		[in]	0=自动档/1=1A档/2=100mA档/3=10mA档
	 *	@retval		返回双精度浮点值，单位为A
	 */	
	virtual	double read_dci(const	DWORD param1)	const;
	///读度直流电压
	/** 
	 *	@param		[in]	0=自动档/1=100V档/2=10V档
	 *	@retval		返回双精度浮点值，单位为V
	 */		
	virtual	double read_dcv(const	DWORD param1)	const;
	///读取仪器ID
	inline	char* get_device_id()	const	{	return	device_id;}
	///在LCD显示一些内容 第一行最多12个字母，第二行最多18个字母
	virtual	void display(const	char*	const param1, const	char*	const param2)	const;
};

#endif  //_AG34410A_H
