#include "FLUKE8508A.h"

double FLUKE8508A::read_dci(DWORD param1) const{
	double	result	=	0;
	const	char*	const	scale0	=	"DCI;RDG?\n";				//自动档
	const	char*	const	scale1	=	"DCI 0.0002,RESL7;RDG?\n";	//2mA档
	const	char*	const	scale2	=	"DCI 0.002,RESL7;RDG?\n";	//20mA档	
	const	char*	const	scale3	=	"DCI 0.02,RESL7;RDG?\n";	//200mA档
	const	char*	const	scale4	=	"DCI 0.2,RESL7;RDG?\n";		//2A档
	
	switch	(param1)
	{
	case 0:
		viQueryf(get_device_res(), (char*)scale0, "%lf", &result);
		break;
	case 1:
		viQueryf(get_device_res(), (char*)scale1, "%lf", &result);
		break;
	case 2:
		viQueryf(get_device_res(), (char*)scale2, "%lf", &result);
		break;
	case 3:
		viQueryf(get_device_res(), (char*)scale3, "%lf", &result);
		break;
	case 4:
		viQueryf(get_device_res(), (char*)scale4, "%lf", &result);
		break;		
	default:
		break;
	}
	return	result;
}

