#if !defined(_CARD_INFO_H)
#define _CARD_INFO_H
#include <Windows.h>
//-----------------------------------------------------------------------
///PB封装窗体的数据结构体接口类
/** 
 *	可用于调用使用PB封装的窗体KIT函数，多数用于操作访问PLX9054的板子
 *	@note		为兼容原有结构体，不能有虚函数，需要增加虚函数的要加在派生类里面
 *	@since		2012.12.16
 */
class card_info {
private:	//深度封装
	///visa资源名字符指针
	char*	card_rscs;			/**< 例如：PXI0::CHASSIS1::SLOT1::INSTR */
	///bar名
	DWORD	mem_bar;			/**< 例如：11==bar0 */
	///bar偏移
	DWORD	mem_offset;			/**< 例如：16位操作时地址边界为0x0, 0x2, 0x4...；32位操作时为0x0, 0x4, 0x8...*/
	///窗体x位置
	DWORD	win_x;				/**< 窗体在屏幕出现的初始位置x坐标 */
	///窗体y位置
	DWORD	win_y;				/**< 窗体在屏幕出现的初始位置y坐标 */
	///窗体的title的字符指针
	char*	win_title;			/**< 用来表明窗体的标题 */
	///数据块的大小
	DWORD	mem_size;			/**< 例如：0x400表示1kbyte大小 */
	///备份2--n/a
	DWORD	backup2;			/**< 未使用，备用 */
	///备份3--n/a
	DWORD	backup3;			/**< 未使用，备用 */
public:
	card_info(const card_info &);
	///copy成员函数
	card_info& operator=(const card_info &card);
	///card_info类默认的构造函数
	/** 
	 *	初始化所有成员变量，同时为字符指针的两个变量申请heap空间，并给空间填入字符
	 */
	card_info();			
	///card_info类默认的析构函数
	/** 
	 *	释放两个字符指针的空间
	 */	
	~card_info();
	///使用另一个字符指针为成员变量card_rscs所指内存区赋值
	/** 
	 *	@param		[in]	param1	字符指针
	 *	@return		void
	 */
	void	set_card_rscs(const	char*	const	param1)	const;
	///使用窗体函数为card_rscs所指内存区赋值
	/** 
	 *	@overload
	 *	@param		[in]	param1	vid如0x10b5
	 *	@param		[in]	param2	did如0x6888
	 *	@retval		true	成功
	 *	@retval		false	失败
	 */
	bool	set_card_rscs(unsigned	short	param1	=	0, unsigned	short	param2	=	0)	const;
	///设置成员变量mem_bar的值
	/** 
	 *	@param		[in]	param1	11==bar0, 13==bar2
	 *	@return		void
	 */
	void	set_mem_bar(DWORD	param1);
	///使用窗体设置成员变量mem_bar的值
	/** 
	 *	@overload
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */
	bool	set_mem_bar();
	///设置成员变量mem_offset的值
	/** 
	 *	@param		[in]	param1	16位边界为0x0, 0x2, 0x4; 32位界为0x0, 0x4, 0x8, 0xC
	 *	@return		void
	 */
	void	set_mem_offset(DWORD	param1);
	///使用窗体设置成员变量mem_offset的值
	/** 
	 *	@overload
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */
	bool	set_mem_offset();
	///设置成员变量mem_size的值
	/** 
	 *	@param		[in]	param1	size，如0x400表示1kbyte
	 *	@return		void
	 */
	void	set_mem_size(DWORD	param1);
	///使用窗体设置成员变量mem_size的值
	/** 
	 *	@overload
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */
	bool	set_mem_size();
	///使用窗体选择使用不同的ram0做为对象，同时影响三个成员变量的值，同时影响win_title指向内存区的值
	/** 
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */
	bool	select_ram0();
	///设置成员变量win_x的值
	/** 
	 *	@param		[in]	param1	窗体x坐标
	 *	@return		void
	 */
	void	set_win_x(DWORD	param1);
	///使用窗体设置成员变量win_x的值
	/** 
	 *	@overload
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */
	bool	set_win_x();
	///设置成员变量win_y的值
	/** 
	 *	@param		[in]	param1	窗体y坐标
	 *	@return		void
	 */
	void	set_win_y(DWORD	param1);
	///使用窗体设置成员变量win_y的值
	/** 
     *	@overload
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */	
	bool	set_win_y();
	///使用另一个字符指针为成员变量win_title所指内存区赋值
	/** 
	 *	@param		[in]	param1	字符指针
	 *	@return		void
	 */
	void	set_win_title(const	char*	const	param1)	const;
	///使用窗体设置成员变量win_title所指内存区的值
	/** 
	 *	@overload
	 *	@retval		true	修改成功
	 *	@retval		false	修改失败
	 */	
	bool	set_win_title()	const;
	///获取成员变量card_rscs的值
	/** 
     * 	@return		成员变量card_rscs
	 */	
	inline	char*	get_card_rscs()	const{	return	card_rscs;}
	///获取成员变量mem_bar的值
	/** 
     * 	@return		成员变量mem_bar
	 */	
	inline	DWORD	get_mem_bar()	const{	return	mem_bar;}
	///获取成员变量mem_offset的值
	/** 
     * 	@return		成员变量mem_offset
	 */	
	inline	DWORD	get_mem_offset()	const{	return	mem_offset;}
	///获取成员变量mem_size的值
	/** 
     * 	@return		成员变量mem_size
	 */	
	inline	DWORD	get_mem_size()	const{	return	mem_size;}
	///获取成员变量win_x的值
	/** 
     * 	@return		成员变量win_x
	 */	
	inline	DWORD	get_win_x()	const{	return	win_x;}
	///获取成员变量win_y的值
	/** 
     * 	@return		成员变量win_y
	 */	
	inline	DWORD	get_win_y()	const{	return	win_y;}
	///获取成员变量win_title的值
	/** 
     * 	@return		成员变量win_title
	 */	
	inline	char*	get_win_title()	const{	return	win_title;}
};

#endif  //_CARD_INFO_H
